mapper.insert:

insert into region (id, remark, region_type, 
  parent_id, name, full_name, 
  region_code, postcode, lastsynctime, 
  createtime, updatetime, data_status
  )
values (#{id,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, #{regionType,jdbcType=TINYINT}, 
  #{parentId,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{fullName,jdbcType=VARCHAR}, 
  #{regionCode,jdbcType=VARCHAR}, #{postcode,jdbcType=VARCHAR}, #{lastsynctime,jdbcType=BIGINT}, 
  #{createtime,jdbcType=BIGINT}, #{updatetime,jdbcType=BIGINT}, #{dataStatus,jdbcType=TINYINT}
  )

mapper.select:
select id, region_type, region_code, `name` from region r where
r.region_type = #{regionType}
and r.region_code like '${parentId}%'
